import React from 'react';
import ReactChartkick, { LineChart, PieChart } from 'react-chartkick';
import Chart from 'chart.js';
import DatetimeRangePicker from 'react-bootstrap-datetimerangepicker';
import moment from 'moment';
import DateRangePicker from 'react-bootstrap-daterangepicker';
// you will need the css that comes with bootstrap@3. if you are using
// a tool like webpack, you can do the following:
import 'bootstrap/dist/css/bootstrap.css';
// you will also need the css that comes with bootstrap-daterangepicker
import 'bootstrap-daterangepicker/daterangepicker.css';

import {
  Button,
} from 'react-bootstrap';

let data = [
  {'name': "Workout", 'data': {"2017-01-01": 5, "2017-01-02": 4}},
  {'name': "Call parents", 'data': {"2017-01-01": 5, "2017-01-02": 3}}
]
data = []
class Chartdraw extends React.Component {
    constructor(props) {
         super(props);

         this.handleApply = this.handleApply.bind(this);
         this.handleStockTicker = this.handleStockTicker(this);

         this.state = {
             'dataingraph': {},
             startDate: moment().subtract(29, 'days'),
             endDate: moment(),
             ranges: {
                 'Today': [moment(), moment()],
                 'Yesterday': [moment().subtract(1, 'days'), moment().subtract(1, 'days')],
                 'Last 7 Days': [moment().subtract(6, 'days'), moment()],
                 'Last 30 Days': [moment().subtract(29, 'days'), moment()],
                 'This Month': [moment().startOf('month'), moment().endOf('month')],
                 'Last Month': [moment().subtract(1, 'month').startOf('month'), moment().subtract(1, 'month').endOf('month')],
             },
             stockTicker: '',
         }

    }

    componentDidMount() {
        console.log('inside componentdidmount')
        console.log(this.state.startDate)
        console.log(this.state.endDate)

        fetch('/ccass/home')
        .then(response =>  response.json())
        .then(resData => {
            data = resData;
            console.log(resData)
            this.setState({'dataingraph': resData})
            })

    }

    handleApply(event, picker) {
    this.setState({
      startDate: picker.startDate,
      endDate: picker.endDate,
    });
    console.log(this.state.startDate.format('YYYY/MM/DD'))
    console.log(this.state.endDate.format('YYYY/MM/DD'))
  }

  handleStockTicker(event) {
      console.log(event)
      this.setState({stockTicker: event.target.value})
  }

    //{data.map((object, i) =>
    //                <LineChart data={[data[6]]} min={144000} max={150000} key={i} />)
    //            }

    //<span className="input-group-btn">
    //                <Button className="default date-range-toggle">
    //                  <i className="fa fa-calendar"/>
    //                </Button>
    //            </span>

    render() {
        let start = this.state.startDate.format('YYYY/MM/DD');
        let end = this.state.endDate.format('YYYY/MM/DD');
        let label = start + ' - ' + end;
        if (start === end) {
            label = start;
        }

        return (
            <div>
                <label className="control-label col-md-3">Date Range Picker</label>
        <div className="col-md-4">
          <DatetimeRangePicker
            startDate={this.state.startDate}
            endDate={this.state.endDate}
            onApply={this.handleApply}
          >
            <div className="input-group">
              <input type="text" classname="stocknumber" onChange={this.handleStockTicker} />
              <input type="text" className="form-control" value={label}/>
              <span className="input-group-btn">
                    <Button className="default date-range-toggle">Click me!
                      <i className="fa fa-calendar"/>
                    </Button>
                </span>
            </div>
          </DatetimeRangePicker>
        </div>
                We will load graph here
                <LineChart data={data} />

                {data.map((object, i) =>
                    <LineChart data={[data[i]]} key={i} />)
                }

                {data.map((object, i) =>
                    <LineChart data={[data[6]]} min={144000} max={150000} key={i} />)
               }



            </div>

        )

    }

}

export default Chartdraw;
